import React, {useEffect} from 'react';
import {useWeather} from "../Context/WeatherContext";
import axios from "axios";
import {Button, Card, Col} from "react-bootstrap";
import {useCity} from "../Context/CityContext";

function Weather() {
    const { setWeathers} = useWeather();
    const {city} = useCity();
    const days = ["Pazartesi","Salı","Çarşamba","Perşembe","Cuma","Cumartesi","Pazar"]
    useEffect(()=>{
        //getCoordinate()
    },[city])

    const getCoordinate = async () => {
        await axios(`https://api.openweathermap.org/data/2.5/weather?q=${city}&lang=tr&appid=4c87fdde1f3bfe4993325a6ae948e319`)
            .then((res) => {
                console.log(res.data.coord)
                getWeatherData(res.data.coord.lon,res.data.coord.lat)
            }
        )
    }

    const getWeatherData = async (lon,lat) => {
        await axios(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=hourly,minutely,current&lang=tr&appid=4c87fdde1f3bfe4993325a6ae948e319`)
            .then((res) => {
                console.log(res.data.daily)
                //setWeathersList([...weathersList,res.data.daily])
                //setWeather([...weather,res.data.daily]);

                setWeathers(res.data.daily)

            })
    }
    return (
        <>

            {days.map((l,i)=>(

                    <Card key={i} style={{ width: '11rem' }}>
                        <Card.Img variant="top" src="" />
                        <Card.Body>

                            <Card.Title>{l}</Card.Title>
                            <Card.Title>{new Date(Date.now(1)).getUTCDay()}</Card.Title>
                            <Card.Title>{new Intl.DateTimeFormat('tr-TR', { weekday: 'long'}).format(new Date(Date.now()).getUTCDay())}</Card.Title>
                            <Card.Text>
                                Some quick example text to build on the card title and make up the bulk of
                                the card's content.
                            </Card.Text>
                            <Button variant="primary">Go somewhere</Button>
                        </Card.Body>
                    </Card>
                )



            )}


        </>
    );
}

export default Weather;
