import React, { useEffect, useState } from "react";
import { useWeather } from "../Context/WeatherContext";
import axios from "axios";
import { Card } from "react-bootstrap";
import { useCity } from "../Context/CityContext";

function Weather() {
  const { weathers, setWeathers } = useWeather();
  const { city } = useCity();
  const [show, setShow] = useState(false);
  const [lat, setLat] = useState();
  const [long, setLong] = useState();

  useEffect(() => {
    function getCoordinateUrl() {
      return `https://api.openweathermap.org/data/2.5/weather?q=${city}&lang=tr&appid=4c87fdde1f3bfe4993325a6ae948e319`;
    }
    async function getCoordinate() {
      await axios(getCoordinateUrl())
        .then((res) => {
          setShow(false);
          setLat(res.data.coord.lat);
          setLong(res.data.coord.long);
          //getWeatherData(res.data.coord.lon, res.data.coord.lat);
        })
        .catch((err) => {
          setShow(true);
        });
    }
    getCoordinate();
  }, [city]);

  async function getWeatherData(lon, lat) {
    await axios(
      `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&units=imperial&exclude=hourly,minutely,current&lang=tr&appid=4c87fdde1f3bfe4993325a6ae948e319`
    )
      .then((res) => {
        setShow(false);
        setWeathers(res.data.daily);
      })
      .catch((err) => {
        setShow(true);
      });
  }

  return (
    <>
      {show === true && (
        <div className="alert alert-danger" role="alert">
          The request limit has been reached.
        </div>
      )}

      {weathers.map((weather, i) => (
        <Card key={i} style={{ width: "9.5rem" }}>
          <Card.Body>
            <Card.Title>
              {" "}
              {new Date(weather.dt * 1000).toLocaleString("en-EN", {
                weekday: "long",
              })}
            </Card.Title>
            <Card.Img
              variant="top"
              src={
                "https://openweathermap.org/img/wn/" +
                weather.weather[0].icon +
                "@2x.png"
              }
            />

            <Card.Text>
              {weather.temp.day + "째"}
              &ensp;
              {weather.clouds + "째"}
              <br />
              <span>{weather.weather[0].description.toLowerCase()}</span>
            </Card.Text>
          </Card.Body>
        </Card>
      ))}
      <br />
    </>
  );
}

export default Weather;
