import React from 'react';
import {useWeather} from "../Context/WeatherContext";
import axios from "axios";

function Weather(props) {
    const {city, setCity,weather, setWeather} = useWeather();
    const getCoordinate =  () => {
         axios(`https://api.openweathermap.org/data/2.5/weather?q=${city}&lang=tr&appid=5f05a8a95cdc6832fe3ac15e25ab1397`)
            .then((res) => {
                console.log(res.data.coord)
                getWeatherData(res.data.coord.lon,res.data.coord.lat)
            }
        )
    }

    const getWeatherData =  (lon,lat) => {

         axios(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=hourly,minutely,current&lang=tr&appid=5f05a8a95cdc6832fe3ac15e25ab1397`)
            .then((res) => {
                console.log(res.data.daily)
                res.data.daily.map((weat)=>{
                    console.log(weat.weather[0].description)
                    setWeather(weat.weather[0].description)
                })
            })
    }
    getCoordinate()
    return (
        <div>

        </div>
    );
}

export default Weather;
