import React, {useEffect} from 'react';
import {cities} from '../api/cities';
import {useWeather} from "../Context/WeatherContext";
function Form() {
    const {city,setCity} = useWeather();
    useEffect(()=>{
        console.log(city)
    },[city])
    return (
        <div>
            <select name="city" >
                {
                    cities.map((city,index)=>(
                        <option key={index} value={city}>{city}</option>
                    ))
                }
            </select>
        </div>
    );
}

export default Form;
