import React from 'react';
import {useWeather} from "../Context/WeatherContext";
import axios from "axios";
import {Button, Card} from "react-bootstrap";

function Weather(props) {
    const {city, setCity,weather, setWeather,} = useWeather();
    const getCoordinate = async () => {
        await axios(`https://api.openweathermap.org/data/2.5/weather?q=${city}&lang=tr&appid=5f05a8a95cdc6832fe3ac15e25ab1397`)
            .then((res) => {
                console.log(res.data.coord)
                getWeatherData(res.data.coord.lon,res.data.coord.lat)
            }
        )
    }

    const getWeatherData = async (lon,lat) => {
        await axios(`https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&exclude=hourly,minutely,current&lang=tr&appid=5f05a8a95cdc6832fe3ac15e25ab1397`)
            .then((res) => {
                console.log(res.data.daily)
                res.data.daily.map((weat)=>{
                    console.log(weat)
                    //setWeather([weat.weather[0].description])
                })
            })
    }
    getCoordinate()
    return (
        <div>
            {weather.map((w)=>(
            <Card style={{ width: '18rem' }}>
                <Card.Img variant="top" src="" />
                <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                        Some quick example text to build on the card title and make up the bulk of
                        the card's content.
                    </Card.Text>
                    <Button variant="primary">Go somewhere</Button>
                </Card.Body>
            </Card>
                ))}

        </div>
    );
}

export default Weather;
